import { nameGenerator } from './nameGenerator';

class PlanetGenerator {
  private readonly planetTypes = [
    'Terrestrial', 'Gas Giant', 'Ice World', 'Desert World', 'Ocean World',
    'Volcanic', 'Crystalline', 'Metal Rich', 'Toxic', 'Artificial'
  ];

  private readonly atmospheres = [
    'Oxygen-Rich', 'Nitrogen-Heavy', 'Methane', 'Carbon Dioxide', 'Toxic Gas',
    'No Atmosphere', 'Hydrogen', 'Noble Gas Mix', 'Corrosive', 'Unknown Composition'
  ];

  private readonly climates = [
    'Tropical', 'Temperate', 'Arctic', 'Desert', 'Variable',
    'Extreme Heat', 'Extreme Cold', 'Constant Storm', 'Calm', 'Chaotic'
  ];

  generate() {
    const name = nameGenerator.generate('planet');
    const type = this.planetTypes[Math.floor(Math.random() * this.planetTypes.length)];
    const atmosphere = this.atmospheres[Math.floor(Math.random() * this.atmospheres.length)];
    const climate = this.climates[Math.floor(Math.random() * this.climates.length)];

    const gravity = Math.round((Math.random() * 2 + 0.3) * 100) / 100; // 0.3 to 2.3 G
    const dayLength = Math.floor(Math.random() * 48) + 12; // 12 to 60 hours
    const temperature = Math.floor(Math.random() * 400) - 100; // -100 to 300¬∞C

    const habitability = this.calculateHabitability(type, atmosphere, climate, gravity, temperature);
    const resources = this.generateResources(type);
    const dangers = this.generateDangers(type, climate);

    return {
      name,
      type,
      atmosphere,
      climate,
      gravity,
      dayLength,
      temperature,
      habitability,
      resources,
      dangers,
      population: this.generatePopulation(habitability),
      points_of_interest: this.generatePointsOfInterest(),
      exploration_difficulty: Math.floor(Math.random() * 5) + 1
    };
  }

  private calculateHabitability(type: string, atmosphere: string, climate: string, gravity: number, temperature: number): number {
    let score = 50;

    // Type modifiers
    if (type === 'Terrestrial') score += 30;
    else if (type === 'Ocean World') score += 20;
    else if (type === 'Desert World') score += 10;
    else if (type === 'Gas Giant') score -= 40;
    else if (type === 'Toxic') score -= 30;

    // Atmosphere modifiers
    if (atmosphere === 'Oxygen-Rich') score += 25;
    else if (atmosphere === 'Nitrogen-Heavy') score += 15;
    else if (atmosphere === 'No Atmosphere') score -= 30;
    else if (atmosphere === 'Toxic Gas') score -= 25;

    // Climate modifiers
    if (climate === 'Temperate') score += 20;
    else if (climate === 'Tropical') score += 10;
    else if (climate === 'Extreme Heat' || climate === 'Extreme Cold') score -= 20;

    // Gravity modifiers
    if (gravity >= 0.8 && gravity <= 1.2) score += 15;
    else if (gravity < 0.5 || gravity > 2.0) score -= 15;

    // Temperature modifiers
    if (temperature >= 0 && temperature <= 30) score += 15;
    else if (temperature < -50 || temperature > 50) score -= 15;

    return Math.max(0, Math.min(100, score));
  }

  private generateResources(type: string) {
    const commonResources = ['Iron Ore', 'Silicon', 'Carbon', 'Water Ice'];
    const rareResources = ['Nexium Crystal', 'Quantum Matter', 'Rare Metals', 'Energy Crystals'];
    const uniqueResources = ['Ancient Artifacts', 'Alien Technology', 'Exotic Matter', 'Time Crystals'];

    const resources = [];

    // Common resources (always present)
    const numCommon = Math.floor(Math.random() * 3) + 1;
    for (let i = 0; i < numCommon; i++) {
      const resource = commonResources[Math.floor(Math.random() * commonResources.length)];
      resources.push({
        name: resource,
        abundance: Math.random(),
        extraction_difficulty: Math.floor(Math.random() * 3) + 1
      });
    }

    // Rare resources (50% chance)
    if (Math.random() > 0.5) {
      const resource = rareResources[Math.floor(Math.random() * rareResources.length)];
      resources.push({
        name: resource,
        abundance: Math.random() * 0.5,
        extraction_difficulty: Math.floor(Math.random() * 3) + 3
      });
    }

    // Unique resources (10% chance)
    if (Math.random() > 0.9) {
      const resource = uniqueResources[Math.floor(Math.random() * uniqueResources.length)];
      resources.push({
        name: resource,
        abundance: Math.random() * 0.2,
        extraction_difficulty: 5
      });
    }

    return resources;
  }

  private generateDangers(type: string, climate: string) {
    const dangers = [];

    if (type === 'Volcanic') {
      dangers.push('Volcanic Activity', 'Toxic Gas Vents', 'Extreme Heat');
    }

    if (type === 'Toxic') {
      dangers.push('Poisonous Atmosphere', 'Corrosive Environment', 'Radiation');
    }

    if (climate === 'Constant Storm') {
      dangers.push('Severe Weather', 'Lightning Storms', 'High Winds');
    }

    if (Math.random() > 0.7) {
      const additionalDangers = [
        'Hostile Wildlife', 'Ancient Guardians', 'Unstable Terrain',
        'Magnetic Anomalies', 'Gravitational Disturbances', 'Energy Storms'
      ];
      dangers.push(additionalDangers[Math.floor(Math.random() * additionalDangers.length)]);
    }

    return dangers;
  }

  private generatePopulation(habitability: number) {
    if (habitability < 20) return 'Uninhabited';
    if (habitability < 40) return 'Research Outpost';
    if (habitability < 60) return 'Small Colony';
    if (habitability < 80) return 'Established Settlement';
    return 'Major Population Center';
  }

  private generatePointsOfInterest() {
    const poi = [
      'Ancient Ruins', 'Crashed Starship', 'Natural Wonder', 'Mining Operation',
      'Research Facility', 'Alien Monolith', 'Energy Anomaly', 'Hidden Cave System',
      'Orbital Debris', 'Strange Formation', 'Underground Lake', 'Crystal Caverns'
    ];

    const interests = [];
    const numPOI = Math.floor(Math.random() * 4);

    for (let i = 0; i < numPOI; i++) {
      const interest = poi[Math.floor(Math.random() * poi.length)];
      if (!interests.includes(interest)) {
        interests.push(interest);
      }
    }

    return interests;
  }
}

export const planetGenerator = new PlanetGenerator();

server/generators/recipeGenerator.ts.content:
interface Recipe {
  name: string;
  type: 'weapon' | 'component' | 'upgrade' | 'consumable' | 'material';
  materials: Array<{ name: string; quantity: number }>;
  result: { name: string; quantity: number; stats?: any };
  level: number;
  rarity: 'common' | 'uncommon' | 'rare' | 'epic' | 'legendary';
  craftingTime: number; // in minutes
  description: string;
  category: string;
}

class RecipeGenerator {
  private readonly materialTypes = {
    basic: ['Iron Ore', 'Silicon', 'Carbon Fiber', 'Aluminum', 'Copper Wire'],
    advanced: ['Titanium Alloy', 'Quantum Steel', 'Plasma Conduit', 'Neural Fiber', 'Energy Cell'],
    rare: ['Nexium Crystal', 'Dark Matter', 'Temporal Crystal', 'Void Essence', 'Quantum Matrix'],
    exotic: ['Living Metal', 'Consciousness Core', 'Reality Shard', 'Infinity Particle', 'Dimensional Anchor']
  };

  private readonly weaponComponents = [
    'Barrel', 'Trigger Assembly', 'Power Core', 'Targeting System', 'Ammunition Feed',
    'Cooling System', 'Stabilizer', 'Charge Capacitor', 'Beam Focuser', 'Projectile Chamber'
  ];

  private readonly weaponTypes = [
    'Laser Rifle', 'Plasma Cannon', 'Ion Blaster', 'Quantum Disruptor', 'Particle Beam',
    'Photon Torpedo', 'Energy Lance', 'Pulse Rifle', 'Gravity Gun', 'Molecular Disassembler'
  ];

  private readonly componentTypes = [
    'Shield Generator', 'Engine Booster', 'Sensor Array', 'Life Support Module',
    'Navigation Computer', 'Communication Array', 'Power Regulator', 'Hull Plating',
    'Magnetic Field Generator', 'Quantum Processor'
  ];

  private readonly upgradeTypes = [
    'Armor Plating', 'Speed Enhancement', 'Weapon Modification', 'Sensor Upgrade',
    'Engine Tuning', 'Shield Booster', 'Cargo Expansion', 'Stealth Module',
    'Tactical Computer', 'Emergency Systems'
  ];

  generate(type?: string, level?: number, rarity?: string): Recipe {
    const recipeType = type as Recipe['type'] || this.getRandomType();
    const recipeLevel = level || Math.floor(Math.random() * 5) + 1;
    const recipeRarity = rarity as Recipe['rarity'] || this.getRandomRarity();

    switch (recipeType) {
      case 'weapon':
        return this.generateWeaponRecipe(recipeLevel, recipeRarity);
      case 'component':
        return this.generateComponentRecipe(recipeLevel, recipeRarity);
      case 'upgrade':
        return this.generateUpgradeRecipe(recipeLevel, recipeRarity);
      case 'consumable':
        return this.generateConsumableRecipe(recipeLevel, recipeRarity);
      case 'material':
        return this.generateMaterialRecipe(recipeLevel, recipeRarity);
      default:
        return this.generateWeaponRecipe(recipeLevel, recipeRarity);
    }
  }

  private getRandomType(): Recipe['type'] {
    const types: Recipe['type'][] = ['weapon', 'component', 'upgrade', 'consumable', 'material'];
    return types[Math.floor(Math.random() * types.length)];
  }

  private getRandomRarity(): Recipe['rarity'] {
    const rarities: Recipe['rarity'][] = ['common', 'uncommon', 'rare', 'epic', 'legendary'];
    const weights = [0.4, 0.3, 0.2, 0.08, 0.02];

    const random = Math.random();
    let total = 0;
    for (let i = 0; i < weights.length; i++) {
      total += weights[i];
      if (random <= total) {
        return rarities[i];
      }
    }
    return 'common';
  }

  private generateWeaponRecipe(level: number, rarity: Recipe['rarity']): Recipe {
    const weaponName = this.weaponTypes[Math.floor(Math.random() * this.weaponTypes.length)];
    const materials = this.generateMaterials(level, rarity, 'weapon');

    const damage = this.calculateWeaponDamage(level, rarity);
    const accuracy = Math.floor(Math.random() * 20) + 70; // 70-90% accuracy
    const critChance = Math.floor(Math.random() * 15) + 5; // 5-20% crit chance

    return {
      name: `${rarity.charAt(0).toUpperCase() + rarity.slice(1)} ${weaponName}`,
      type: 'weapon',
      materials,
      result: {
        name: weaponName,
        quantity: 1,
        stats: {
          damage,
          accuracy,
          critChance,
          range: Math.floor(Math.random() * 500) + 200 // 200-700 range
        }
      },
      level,
      rarity,
      craftingTime: this.calculateCraftingTime(rarity, 'weapon'),
      description: `A ${rarity} grade ${weaponName.toLowerCase()} designed for space combat. Deals ${damage} damage with ${accuracy}% accuracy.`,
      category: 'Weapons'
    };
  }

  private generateComponentRecipe(level: number, rarity: Recipe['rarity']): Recipe {
    const componentName = this.componentTypes[Math.floor(Math.random() * this.componentTypes.length)];
    const materials = this.generateMaterials(level, rarity, 'component');

    const bonus = this.calculateComponentBonus(level, rarity);

    return {
      name: `${rarity.charAt(0).toUpperCase() + rarity.slice(1)} ${componentName}`,
      type: 'component',
      materials,
      result: {
        name: componentName,
        quantity: 1,
        stats: {
          efficiency: bonus,
          durability: Math.floor(Math.random() * 1000) + 500,
          powerConsumption: Math.floor(Math.random() * 50) + 10
        }
      },
      level,
      rarity,
      craftingTime: this.calculateCraftingTime(rarity, 'component'),
      description: `A ${rarity} ${componentName.toLowerCase()} that provides ${bonus}% efficiency bonus to ship systems.`,
      category: 'Ship Components'
    };
  }

  private generateUpgradeRecipe(level: number, rarity: Recipe['rarity']): Recipe {
    const upgradeName = this.upgradeTypes[Math.floor(Math.random() * this.upgradeTypes.length)];
    const materials = this.generateMaterials(level, rarity, 'upgrade');

    const bonusValue = this.calculateUpgradeBonus(level, rarity);

    return {
      name: `${rarity.charAt(0).toUpperCase() + rarity.slice(1)} ${upgradeName}`,
      type: 'upgrade',
      materials,
      result: {
        name: upgradeName,
        quantity: 1,
        stats: {
          bonusType: this.getUpgradeBonusType(upgradeName),
          bonusValue,
          installationCost: Math.floor(Math.random() * 1000) + 200
        }
      },
      level,
      rarity,
      craftingTime: this.calculateCraftingTime(rarity, 'upgrade'),
      description: `A ${rarity} upgrade module that enhances ship performance. Provides +${bonusValue} to relevant systems.`,
      category: 'Ship Upgrades'
    };
  }

  private generateConsumableRecipe(level: number, rarity: Recipe['rarity']): Recipe {
    const consumables = [
      'Repair Kit', 'Shield Battery', 'Energy Boost', 'Hull Sealant', 'System Stabilizer',
      'Emergency Oxygen', 'Nano-repair Swarm', 'Power Cell', 'Medical Kit', 'Fuel Injector'
    ];

    const consumableName = consumables[Math.floor(Math.random() * consumables.length)];
    const materials = this.generateMaterials(level, rarity, 'consumable');

    const quantity = Math.floor(Math.random() * 5) + 1; // 1-5 items per craft
    const effectValue = this.calculateConsumableEffect(level, rarity);

    return {
      name: `${rarity.charAt(0).toUpperCase() + rarity.slice(1)} ${consumableName}`,
      type: 'consumable',
      materials,
      result: {
        name: consumableName,
        quantity,
        stats: {
          effect: this.getConsumableEffect(consumableName),
          value: effectValue,
          uses: 1
        }
      },
      level,
      rarity,
      craftingTime: this.calculateCraftingTime(rarity, 'consumable'),
      description: `A ${rarity} ${consumableName.toLowerCase()} that provides ${effectValue} points of restoration when used.`,
      category: 'Consumables'
    };
  }

  private generateMaterialRecipe(level: number, rarity: Recipe['rarity']): Recipe {
    const materialName = `Processed ${rarity.charAt(0).toUpperCase() + rarity.slice(1)} Alloy`;
    const rawMaterials = this.generateRawMaterials(level, rarity);

    const quantity = Math.floor(Math.random() * 3) + 1; // 1-3 processed materials

    return {
      name: materialName,
      type: 'material',
      materials: rawMaterials,
      result: {
        name: materialName,
        quantity,
        stats: {
          purity: Math.floor(Math.random() * 20) + 80, // 80-100% purity
          marketValue: this.calculateMaterialValue(level, rarity)
        }
      },
      level,
      rarity,
      craftingTime: this.calculateCraftingTime(rarity, 'material'),
      description: `A refined ${rarity} alloy suitable for advanced crafting projects. Higher purity materials yield better results.`,
      category: 'Refined Materials'
    };
  }

  private generateMaterials(level: number, rarity: Recipe['rarity'], recipeType: string): Array<{ name: string; quantity: number }> {
    const materials: Array<{ name: string; quantity: number }> = [];

    // Base materials needed
    const numMaterials = Math.min(6, Math.floor(level / 2) + 2 + (rarity === 'legendary' ? 2 : 0));

    // Add basic materials
    const basicMaterial = this.materialTypes.basic[Math.floor(Math.random() * this.materialTypes.basic.length)];
    materials.push({ name: basicMaterial, quantity: Math.floor(Math.random() * 5) + 1 });

    // Add materials based on level and rarity
    if (level >= 2) {
      const advancedMaterial = this.materialTypes.advanced[Math.floor(Math.random() * this.materialTypes.advanced.length)];
      materials.push({ name: advancedMaterial, quantity: Math.floor(Math.random() * 3) + 1 });
    }

    if (level >= 3 || ['rare', 'epic', 'legendary'].includes(rarity)) {
      const rareMaterial = this.materialTypes.rare[Math.floor(Math.random() * this.materialTypes.rare.length)];
      materials.push({ name: rareMaterial, quantity: Math.floor(Math.random() * 2) + 1 });
    }

    if (rarity === 'legendary') {
      const exoticMaterial = this.materialTypes.exotic[Math.floor(Math.random() * this.materialTypes.exotic.length)];
      materials.push({ name: exoticMaterial, quantity: 1 });
    }

    // Add specific components for weapons
    if (recipeType === 'weapon') {
      const component = this.weaponComponents[Math.floor(Math.random() * this.weaponComponents.length)];
      materials.push({ name: component, quantity: 1 });
    }

    return materials;
  }

  private generateRawMaterials(level: number, rarity: Recipe['rarity']): Array<{ name: string; quantity: number }> {
    const materials: Array<{ name: string; quantity: number }> = [];

    // More raw materials needed for processing
    const quantity = Math.floor(Math.random() * 10) + 5; // 5-15 raw materials

    let materialPool = this.materialTypes.basic;
    if (rarity === 'uncommon' || rarity === 'rare') materialPool = this.materialTypes.advanced;
    if (rarity === 'epic' || rarity === 'legendary') materialPool = this.materialTypes.rare;

    const material = materialPool[Math.floor(Math.random() * materialPool.length)];
    materials.push({ name: `Raw ${material}`, quantity });

    return materials;
  }

  private calculateWeaponDamage(level: number, rarity: Recipe['rarity']): number {
    const baseRarityMultipliers = {
      'common': 1.0,
      'uncommon': 1.2,
      'rare': 1.5,
      'epic': 1.8,
      'legendary': 2.2
    };

    const baseDamage = 50;
    const levelMultiplier = 1 + (level - 1) * 0.2;
    const rarityMultiplier = baseRarityMultipliers[rarity];

    return Math.floor(baseDamage * levelMultiplier * rarityMultiplier);
  }

  private calculateComponentBonus(level: number, rarity: Recipe['rarity']): number {
    const baseRarityBonus = {
      'common': 5,
      'uncommon': 10,
      'rare': 20,
      'epic': 35,
      'legendary': 50
    };

    return baseRarityBonus[rarity] + (level - 1) * 5;
  }

  private calculateUpgradeBonus(level: number, rarity: Recipe['rarity']): number {
    const baseRarityBonus = {
      'common': 10,
      'uncommon': 20,
      'rare': 35,
      'epic': 55,
      'legendary': 80
    };

    return baseRarityBonus[rarity] + (level - 1) * 10;
  }

  private calculateConsumableEffect(level: number, rarity: Recipe['rarity']): number {
    const baseRarityEffect = {
      'common': 50,
      'uncommon': 100,
      'rare': 200,
      'epic': 350,
      'legendary': 500
    };

    return baseRarityEffect[rarity] + (level - 1) * 25;
  }

  private calculateMaterialValue(level: number, rarity: Recipe['rarity']): number {
    const baseRarityValue = {
      'common': 25,
      'uncommon': 75,
      'rare': 200,
      'epic': 500,
      'legendary': 1000
    };

    return baseRarityValue[rarity] + (level - 1) * 50;
  }

  private calculateCraftingTime(rarity: Recipe['rarity'], type: string): number {
    const baseTime = {
      'common': 5,
      'uncommon': 15,
      'rare': 30,
      'epic': 60,
      'legendary': 120
    };

    const typeMultiplier = {
      'weapon': 1.5,
      'component': 1.2,
      'upgrade': 1.0,
      'consumable': 0.5,
      'material': 0.8
    };

    return Math.floor(baseTime[rarity] * typeMultiplier[type as keyof typeof typeMultiplier]);
  }

  private getUpgradeBonusType(upgradeName: string): string {
    const bonusMapping: { [key: string]: string } = {
      'Armor Plating': 'health',
      'Speed Enhancement': 'speed',
      'Weapon Modification': 'damage',
      'Sensor Upgrade': 'sensors',
      'Engine Tuning': 'speed',
      'Shield Booster': 'shields',
      'Cargo Expansion': 'cargo',
      'Stealth Module': 'stealth',
      'Tactical Computer': 'accuracy',
      'Emergency Systems': 'survival'
    };

    return bonusMapping[upgradeName] || 'general';
  }

  private getConsumableEffect(consumableName: string): string {
    const effectMapping: { [key: string]: string } = {
      'Repair Kit': 'hull_repair',
      'Shield Battery': 'shield_restore',
      'Energy Boost': 'energy_restore',
      'Hull Sealant': 'emergency_repair',
      'System Stabilizer': 'system_repair',
      'Emergency Oxygen': 'life_support',
      'Nano-repair Swarm': 'auto_repair',
      'Power Cell': 'power_boost',
      'Medical Kit': 'crew_heal',
      'Fuel Injector': 'fuel_efficiency'
    };

    return effectMapping[consumableName] || 'general_boost';
  }

  generateRecipeBook(level: number = 1): Recipe[] {
    const recipes: Recipe[] = [];
    const types: Recipe['type'][] = ['weapon', 'component', 'upgrade', 'consumable', 'material'];

    types.forEach(type => {
      for (let i = 1; i <= Math.min(level, 5); i++) {
        const numRecipes = type === 'weapon' ? 3 : 2;
        for (let j = 0; j < numRecipes; j++) {
          recipes.push(this.generate(type, i));
        }
      }
    });

    return recipes.sort((a, b) => {
      if (a.level !== b.level) return a.level - b.level;
      return a.name.localeCompare(b.name);
    });
  }
}

export const recipeGenerator = new RecipeGenerator();

server/services/combatSystem.ts.content:
import { storage } from '../storage';
import { gameEngine } from './gameEngine';
import { contentGenerator } from '../generators/contentGenerator';
import { rewardCalculator } from '../utils/rewardCalculator';

interface CombatResult {
  winner: string;
  attackerDamage: number;
  defenderDamage: number;
  rewards: Array<{ type: string; name: string; quantity: number; value: number }>;
  experience: number;
}

class CombatSystem {
  async pveCompat(userId: string, enemyType: string = 'random') {
    const user = await storage.getUser(userId);
    if (!user) {
      throw new Error('User not found');
    }

    const ships = await storage.getUserShips(userId);
    const activeShip = ships.find(ship => ship.isActive);
    if (!activeShip) {
      throw new Error('No active ship found');
    }

    const enemy = contentGenerator.generateEnemy(enemyType, user.level);
    const result = this.simulateCombat(activeShip, enemy);

    // Calculate rewards based on victory
    const rewards = result.winner === 'player' ? 
      rewardCalculator.calculateCombatRewards(enemy.difficulty, user.level) : [];

    // Apply ship damage
    const newHealth = Math.max(0, activeShip.health - result.defenderDamage);
    await storage.updateShip(activeShip.id, { health: newHealth });

    // Award experience and rewards
    await gameEngine.gainExperience(userId, result.experience);

    if (rewards.length > 0) {
      for (const reward of rewards) {
        if (reward.type === 'credits') {
          await storage.updateUser(userId, { 
            credits: user.credits + reward.value 
          });
        } else if (reward.type === 'nexium') {
          await storage.updateUser(userId, { 
            nexium: user.nexium + reward.quantity 
          });
        } else {
          await storage.addResource({
            userId,
            name: reward.name,
            type: reward.type,
            quantity: reward.quantity,
            rarity: this.determineRarity(reward.value),
            value: reward.value
          });
        }
      }
    }

    // Log combat
    const combatLog = await storage.addCombatLog({
      attackerId: userId,
      defenderId: null,
      type: 'pve',
      result: {
        winner: result.winner === 'player' ? userId : 'enemy',
        attackerDamage: result.attackerDamage,
        defenderDamage: result.defenderDamage,
        rewards,
        experience: result.experience
      }
    });

    return {
      success: result.winner === 'player',
      enemy: enemy.name,
      result: combatLog.result,
      shipHealth: newHealth
    };
  }

  async pvpCombat(attackerId: string, defenderId: string) {
    if (attackerId === defenderId) {
      throw new Error('Cannot attack yourself');
    }

    const attacker = await storage.getUser(attackerId);
    const defender = await storage.getUser(defenderId);

    if (!attacker || !defender) {
      throw new Error('One or both users not found');
    }

    const attackerShips = await storage.getUserShips(attackerId);
    const defenderShips = await storage.getUserShips(defenderId);

    const attackerShip = attackerShips.find(ship => ship.isActive);
    const defenderShip = defenderShips.find(ship => ship.isActive);

    if (!attackerShip || !defenderShip) {
      throw new Error('One or both players do not have an active ship');
    }

    const result = this.simulatePlayerCombat(attackerShip, defenderShip);

    // Apply damage to both ships
    const attackerNewHealth = Math.max(0, attackerShip.health - result.attackerDamageReceived);
    const defenderNewHealth = Math.max(0, defenderShip.health - result.defenderDamageReceived);

    await storage.updateShip(attackerShip.id, { health: attackerNewHealth });
    await storage.updateShip(defenderShip.id, { health: defenderNewHealth });

    // Award experience to both players
    const winnerExp = 150;
    const loserExp = 50;

    if (result.winner === 'attacker') {
      await gameEngine.gainExperience(attackerId, winnerExp);
      await gameEngine.gainExperience(defenderId, loserExp);
    } else {
      await gameEngine.gainExperience(attackerId, loserExp);
      await gameEngine.gainExperience(defenderId, winnerExp);
    }

    // Log combat
    const combatLog = await storage.addCombatLog({
      attackerId,
      defenderId,
      type: 'pvp',
      result: {
        winner: result.winner === 'attacker' ? attackerId : defenderId,
        attackerDamage: result.attackerDamageDealt,
        defenderDamage: result.defenderDamageDealt,
        rewards: [],
        experience: result.winner === 'attacker' ? winnerExp : loserExp
      }
    });

    return {
      winner: result.winner,
      attackerShipHealth: attackerNewHealth,
      defenderShipHealth: defenderNewHealth,
      result: combatLog.result
    };
  }

  private simulateCombat(playerShip: any, enemy: any) {
    const playerPower = this.calculateShipPower(playerShip);
    const enemyPower = enemy.power;

    // Combat simulation with randomness
    const playerRoll = Math.random() * playerPower;
    const enemyRoll = Math.random() * enemyPower;

    const attackerDamage = Math.floor(playerRoll * 0.3 + playerShip.weapons * 10);
    const defenderDamage = Math.floor(enemyRoll * 0.2 + enemy.weapons * 8);

    const winner = playerRoll > enemyRoll ? 'player' : 'enemy';
    const experience = Math.floor(enemy.difficulty * 25 + (winner === 'player' ? 50 : 25));

    return {
      winner,
      attackerDamage,
      defenderDamage,
      experience
    };
  }

  private simulatePlayerCombat(attackerShip: any, defenderShip: any) {
    const attackerPower = this.calculateShipPower(attackerShip);
    const defenderPower = this.calculateShipPower(defenderShip);

    // PvP combat simulation
    const attackerRoll = Math.random() * attackerPower;
    const defenderRoll = Math.random() * defenderPower;

    const attackerDamageDealt = Math.floor(attackerRoll * 0.25 + attackerShip.weapons * 12);
    const defenderDamageDealt = Math.floor(defenderRoll * 0.25 + defenderShip.weapons * 12);

    // Counter-damage based on ship stats
    const attackerDamageReceived = Math.floor(defenderDamageDealt * (1 - attackerShip.speed / 200));
    const defenderDamageReceived = Math.floor(attackerDamageDealt * (1 - defenderShip.speed / 200));

    const winner = attackerRoll > defenderRoll ? 'attacker' : 'defender';

    return {
      winner,
      attackerDamageDealt,
      defenderDamageDealt,
      attackerDamageReceived,
      defenderDamageReceived
    };
  }

  private calculateShipPower(ship: any): number {
    return ship.health + ship.speed + ship.weapons * 20 + ship.sensors;
  }

  private determineRarity(value: number): string {
    if (value < 10) return 'common';
    if (value < 50) return 'uncommon';
    if (value < 200) return 'rare';
    if (value < 500) return 'epic';
    return 'legendary';
  }
}

export const combatSystem = new CombatSystem();

server/services/economySystem.ts.content:
import { storage } from '../storage';
import { contentGenerator } from '../generators/contentGenerator';

interface MarketItem {
  name: string;
  type: string;
  price: number;
  availability: number;
  rarity: string;
  description: string;
}

class EconomySystem {
  private marketItems: MarketItem[] = [];
  private lastMarketUpdate: number = 0;
  private readonly MARKET_UPDATE_INTERVAL = 3600000; // 1 hour

  constructor() {
    this.initializeMarket();
  }

  private initializeMarket() {
    this.marketItems = [
      {
        name: 'Quantum Core',
        type: 'component',
        price: 2500,
        availability: 10,
        rarity: 'rare',
        description: 'Advanced quantum processing unit'
      },
      {
        name: 'Nexium Crystal',
        type: 'material',
        price: 180,
        availability: 50,
        rarity: 'uncommon',
        description: 'Raw nexium crystal ore'
      },
      {
        name: 'Plasma Cannon',
        type: 'weapon',
        price: 5000,
        availability: 5,
        rarity: 'epic',
        description: 'High-energy plasma weapon system'
      },
      {
        name: 'Shield Generator',
        type: 'component',
        price: 3200,
        availability: 8,
        rarity: 'rare',
        description: 'Deflector shield technology'
      },
      {
        name: 'Hyperspace Fuel',
        type: 'material',
        price: 75,
        availability: 100,
        rarity: 'common',
        description: 'Standard hyperspace travel fuel'
      }
    ];
    this.lastMarketUpdate = Date.now();
  }

  async getMarketItems(): Promise<MarketItem[]> {
    // Update market prices periodically
    if (Date.now() - this.lastMarketUpdate > this.MARKET_UPDATE_INTERVAL) {
      this.updateMarketPrices();
    }
    return this.marketItems;
  }

  private updateMarketPrices() {
    this.marketItems.forEach(item => {
      // Fluctuate prices by ¬±15%
      const fluctuation = (Math.random() - 0.5) * 0.3;
      item.price = Math.floor(item.price * (1 + fluctuation));

      // Update availability
      item.availability = Math.max(0, item.availability + Math.floor(Math.random() * 10) - 5);
    });
    this.lastMarketUpdate = Date.now();
  }

  async buyItem(userId: string, itemName: string, quantity: number) {
    const user = await storage.getUser(userId);
    if (!user) {
      throw new Error('User not found');
    }

    const item = this.marketItems.find(i => i.name === itemName);
    if (!item) {
      throw new Error('Item not found in market');
    }

    if (item.availability < quantity) {
      throw new Error('Insufficient item availability');
    }

    const totalCost = item.price * quantity;
    if (user.credits < totalCost) {
      throw new Error('Insufficient credits');
    }

    // Deduct credits
    await storage.updateUser(userId, {
      credits: user.credits - totalCost
    });

    // Add item to user inventory
    await storage.addResource({
      userId,
      name: item.name,
      type: item.type,
      quantity,
      rarity: item.rarity,
      value: item.price
    });

    // Update market availability
    item.availability -= quantity;

    // Log transaction
    await storage.addMarketTransaction({
      sellerId: null, // NPC transaction
      buyerId: userId,
      itemType: item.type,
      itemName: item.name,
      quantity,
      pricePerUnit: item.price,
      totalPrice: totalCost
    });

    return {
      success: true,
      item: item.name,
      quantity,
      totalCost,
      remainingCredits: user.credits - totalCost
    };
  }

  async sellResource(userId: string, resourceId: string, quantity: number, pricePerUnit: number) {
    const resource = await storage.getUserResources(userId);
    const targetResource = resource.find(r => r.id === resourceId);

    if (!targetResource) {
      throw new Error('Resource not found');
    }

    if (targetResource.quantity < quantity) {
      throw new Error('Insufficient resource quantity');
    }

    const totalIncome = pricePerUnit * quantity;

    // Update user credits
    const user = await storage.getUser(userId);
    if (!user) {
      throw new Error('User not found');
    }

    await storage.updateUser(userId, {
      credits: user.credits + totalIncome
    });

    // Update or remove resource
    if (targetResource.quantity === quantity) {
      await storage.removeResource(resourceId);
    } else {
      await storage.updateResource(resourceId, {
        quantity: targetResource.quantity - quantity
      });
    }

    // Log transaction
    await storage.addMarketTransaction({
      sellerId: userId,
      buyerId: userId, // Player-to-market sale
      itemType: targetResource.type,
      itemName: targetResource.name,
      quantity,
      pricePerUnit,
      totalPrice: totalIncome
    });

    // Update user stats
    await storage.updateUser(userId, {
      stats: {
        ...user.stats,
        trades: user.stats.trades + 1
      }
    });

    return {
      success: true,
      resourceSold: targetResource.name,
      quantitySold: quantity,
      income: totalIncome,
      newCredits: user.credits + totalIncome
    };
  }

  async craftItem(userId: string, recipeId: string) {
    const user = await storage.getUser(userId);
    if (!user) {
      throw new Error('User not found');
    }

    const recipes = await storage.getAllRecipes();
    const recipe = recipes.find(r => r.id === recipeId);
    if (!recipe) {
      throw new Error('Recipe not found');
    }

    const userResources = await storage.getUserResources(userId);

    // Check if user has all required materials
    const materialCheck = recipe.materials?.every(material => {
      const userMaterial = userResources.find(r => r.name === material.name);
      return userMaterial && userMaterial.quantity >= material.quantity;
    });

    if (!materialCheck) {
      throw new Error('Insufficient materials for crafting');
    }

    // Consume materials
    if (recipe.materials) {
      for (const material of recipe.materials) {
        const userMaterial = userResources.find(r => r.name === material.name);
        if (userMaterial) {
          if (userMaterial.quantity === material.quantity) {
            await storage.removeResource(userMaterial.id);
          } else {
            await storage.updateResource(userMaterial.id, {
              quantity: userMaterial.quantity - material.quantity
            });
          }
        }
      }
    }

    // Create result item
    if (recipe.result) {
      await storage.addResource({
        userId,
        name: recipe.result.name,
        type: recipe.type,
        quantity: recipe.result.quantity,
        rarity: recipe.rarity,
        value: this.calculateCraftedItemValue(recipe)
      });
    }

    return {
      success: true,
      crafted: recipe.result?.name,
      quantity: recipe.result?.quantity
    };
  }

  private calculateCraftedItemValue(recipe: any): number {
    const baseValue = {
      common: 50,
      uncommon: 150,
      rare: 400,
      epic: 800,
      legendary: 1500
    };
    return baseValue[recipe.rarity as keyof typeof baseValue] || 100;
  }

  async getMarketHistory(limit: number = 20) {
    return await storage.getMarketHistory(limit);
  }

  async generateDailyDeals(userLevel: number) {
    return contentGenerator.generateDailyMarketDeals(userLevel);
  }
}

export const economySystem = new EconomySystem();

