import { storage } from '../storage';
import { InsertUser, InsertShip } from '@shared/schema';
import { contentGenerator } from '../generators/contentGenerator';
import { rewardCalculator } from '../utils/rewardCalculator';

// Ship tier configurations from the attached file
const SHIP_CONFIGS = {
  scout: {
    1: { variant: 'Swiftwing', health: 100, speed: 80, cargo: 20, weapons: 1, sensors: 60, cost: 0 },
    2: { variant: 'Spectre', health: 120, speed: 90, cargo: 25, weapons: 1, sensors: 70, cost: 500, nexium: 10 },
    3: { variant: 'Phantom', health: 140, speed: 100, cargo: 30, weapons: 2, sensors: 80, cost: 1200, nexium: 25 },
    4: { variant: 'Celestial Whisper', health: 160, speed: 110, cargo: 35, weapons: 2, sensors: 90, cost: 2500, nexium: 50 }
  },
  fighter: {
    1: { variant: 'Vindicator', health: 150, speed: 70, cargo: 15, weapons: 3, sensors: 40, cost: 0 },
    2: { variant: 'Gladiator', health: 180, speed: 75, cargo: 18, weapons: 4, sensors: 45, cost: 600, nexium: 15 },
    3: { variant: 'Annihilator', health: 210, speed: 80, cargo: 21, weapons: 5, sensors: 50, cost: 1400, nexium: 30 },
    4: { variant: 'Dominator', health: 240, speed: 85, cargo: 24, weapons: 6, sensors: 55, cost: 3000, nexium: 60 }
  },
  freighter: {
    1: { variant: 'Hauler', health: 200, speed: 50, cargo: 100, weapons: 1, sensors: 30, cost: 0 },
    2: { variant: 'Bulkhead', health: 240, speed: 55, cargo: 125, weapons: 1, sensors: 35, cost: 700, nexium: 20 },
    3: { variant: 'Citadel', health: 280, speed: 60, cargo: 150, weapons: 2, sensors: 40, cost: 1600, nexium: 35 },
    4: { variant: 'Goliath', health: 320, speed: 65, cargo: 175, weapons: 2, sensors: 45, cost: 3500, nexium: 70 }
  },
  explorer: {
    1: { variant: 'Pathfinder', health: 120, speed: 60, cargo: 40, weapons: 2, sensors: 80, cost: 0 },
    2: { variant: 'Horizon Seeker', health: 140, speed: 65, cargo: 50, weapons: 2, sensors: 90, cost: 550, nexium: 12 },
    3: { variant: 'Nebula Navigator', health: 160, speed: 70, cargo: 60, weapons: 3, sensors: 100, cost: 1300, nexium: 28 },
    4: { variant: 'Event Horizon', health: 180, speed: 75, cargo: 70, weapons: 3, sensors: 110, cost: 2800, nexium: 55 }
  },
  battlecruiser: {
    1: { variant: 'Warden', health: 300, speed: 40, cargo: 30, weapons: 5, sensors: 50, cost: 0 },
    2: { variant: 'Juggernaut', health: 360, speed: 42, cargo: 35, weapons: 6, sensors: 55, cost: 800, nexium: 18 },
    3: { variant: 'Dreadnought', health: 420, speed: 44, cargo: 40, weapons: 7, sensors: 60, cost: 1800, nexium: 40 },
    4: { variant: 'Behemoth', health: 480, speed: 46, cargo: 45, weapons: 8, sensors: 65, cost: 4000, nexium: 80 }
  },
  flagship: {
    1: { variant: 'Sovereign', health: 250, speed: 55, cargo: 50, weapons: 4, sensors: 70, cost: 0 },
    2: { variant: 'Paragon', health: 300, speed: 58, cargo: 60, weapons: 5, sensors: 75, cost: 750, nexium: 16 },
    3: { variant: 'Leviathan', health: 350, speed: 61, cargo: 70, weapons: 6, sensors: 80, cost: 1700, nexium: 38 },
    4: { variant: 'Imperator', health: 400, speed: 64, cargo: 80, weapons: 7, sensors: 85, cost: 3800, nexium: 75 }
  }
};

class GameEngine {
  async registerUser(userData: InsertUser) {
    const user = await storage.createUser(userData);

    // Create starting ship (Swiftwing Scout)
    const startingShip = await this.createStartingShip(user.id);

    // Set the starting ship as active
    await storage.setActiveShip(user.id, startingShip.id);

    // Add starting resources
    await this.giveStartingResources(user.id);

    return user;
  }

  private async createStartingShip(userId: string): Promise<any> {
    const shipConfig = SHIP_CONFIGS.scout[1];

    const shipData: InsertShip = {
      userId,
      name: `${shipConfig.variant}-${Math.random().toString(36).substr(2, 4)}`,
      type: 'scout',
      tier: 1,
      variant: shipConfig.variant,
      health: shipConfig.health,
      maxHealth: shipConfig.health,
      speed: shipConfig.speed,
      cargo: shipConfig.cargo,
      weapons: shipConfig.weapons,
      sensors: shipConfig.sensors,
      isActive: true
    };

    return await storage.createShip(shipData);
  }

  private async giveStartingResources(userId: string) {
    const startingResources = [
      { name: 'Iron Ore', type: 'material', quantity: 10, rarity: 'common', value: 5 },
      { name: 'Energy Cell', type: 'component', quantity: 5, rarity: 'common', value: 15 },
      { name: 'Basic Alloy', type: 'material', quantity: 3, rarity: 'uncommon', value: 25 }
    ];

    for (const resource of startingResources) {
      await storage.addResource({
        userId,
        ...resource
      });
    }
  }

  async upgradeShip(userId: string, shipId: string) {
    const ship = await storage.getShip(shipId);
    if (!ship || ship.userId !== userId) {
      throw new Error('Ship not found or not owned by user');
    }

    if (ship.tier >= 4) {
      throw new Error('Ship is already at maximum tier');
    }

    const user = await storage.getUser(userId);
    if (!user) {
      throw new Error('User not found');
    }

    const shipType = ship.type as keyof typeof SHIP_CONFIGS;
    const nextTier = (ship.tier + 1) as 1 | 2 | 3 | 4;
    const upgradeConfig = SHIP_CONFIGS[shipType][nextTier];

    // Check if user has enough resources
    if (user.credits < upgradeConfig.cost) {
      throw new Error('Insufficient credits');
    }
    if (user.nexium < upgradeConfig.nexium) {
      throw new Error('Insufficient nexium crystals');
    }

    // Deduct resources
    await storage.updateUser(userId, {
      credits: user.credits - upgradeConfig.cost,
      nexium: user.nexium - upgradeConfig.nexium
    });

    // Upgrade ship
    const upgradedShip = await storage.updateShip(shipId, {
      tier: nextTier,
      variant: upgradeConfig.variant,
      health: upgradeConfig.health,
      maxHealth: upgradeConfig.health,
      speed: upgradeConfig.speed,
      cargo: upgradeConfig.cargo,
      weapons: upgradeConfig.weapons,
      sensors: upgradeConfig.sensors
    });

    return {
      success: true,
      ship: upgradedShip,
      costPaid: { credits: upgradeConfig.cost, nexium: upgradeConfig.nexium }
    };
  }

  async purchaseShip(userId: string, shipType: string) {
    const user = await storage.getUser(userId);
    if (!user) {
      throw new Error('User not found');
    }

    if (!(shipType in SHIP_CONFIGS)) {
      throw new Error('Invalid ship type');
    }

    const shipConfig = SHIP_CONFIGS[shipType as keyof typeof SHIP_CONFIGS][1];
    const baseCost = this.getShipBaseCost(shipType);

    if (user.credits < baseCost) {
      throw new Error('Insufficient credits');
    }

    // Deduct cost
    await storage.updateUser(userId, {
      credits: user.credits - baseCost
    });

    // Create new ship
    const shipData: InsertShip = {
      userId,
      name: `${shipConfig.variant}-${Math.random().toString(36).substr(2, 4)}`,
      type: shipType,
      tier: 1,
      variant: shipConfig.variant,
      health: shipConfig.health,
      maxHealth: shipConfig.health,
      speed: shipConfig.speed,
      cargo: shipConfig.cargo,
      weapons: shipConfig.weapons,
      sensors: shipConfig.sensors,
      isActive: false
    };

    const newShip = await storage.createShip(shipData);

    return {
      success: true,
      ship: newShip,
      costPaid: baseCost
    };
  }

  private getShipBaseCost(shipType: string): number {
    const baseCosts = {
      scout: 0, // Free starting ship
      fighter: 5000,
      freighter: 8000,
      explorer: 6000,
      battlecruiser: 15000,
      flagship: 25000
    };
    return baseCosts[shipType as keyof typeof baseCosts] || 1000;
  }

  async repairShip(userId: string, shipId: string) {
    const ship = await storage.getShip(shipId);
    if (!ship || ship.userId !== userId) {
      throw new Error('Ship not found or not owned by user');
    }

    if (ship.health >= ship.maxHealth) {
      throw new Error('Ship is already at full health');
    }

    const user = await storage.getUser(userId);
    if (!user) {
      throw new Error('User not found');
    }

    const repairCost = Math.floor((ship.maxHealth - ship.health) * 10);

    if (user.credits < repairCost) {
      throw new Error('Insufficient credits for repair');
    }

    await storage.updateUser(userId, {
      credits: user.credits - repairCost
    });

    const repairedShip = await storage.updateShip(shipId, {
      health: ship.maxHealth
    });

    return {
      success: true,
      ship: repairedShip,
      repairCost
    };
  }

  async gainExperience(userId: string, amount: number) {
    const user = await storage.getUser(userId);
    if (!user) return;

    const newExperience = user.experience + amount;
    const newLevel = Math.floor(newExperience / 1000) + 1;

    const leveledUp = newLevel > user.level;

    await storage.updateUser(userId, {
      experience: newExperience,
      level: newLevel
    });

    return {
      experienceGained: amount,
      newLevel,
      leveledUp,
      rewards: leveledUp ? rewardCalculator.calculateLevelUpRewards(newLevel) : []
    };
  }
}

export const gameEngine = new GameEngine();

server/services/guildSystem.ts.content:
import { storage } from '../storage';

// Default guild factions as mentioned in the requirements
const DEFAULT_GUILDS = [
  {
    name: 'Stellar Dominion',
    type: 'military',
    description: 'Military empire focused on combat and expansion',
    leaderId: 'npc_dominion_leader'
  },
  {
    name: 'Cosmic Traders',
    type: 'trade',
    description: 'Merchant guild specializing in commerce and trade routes',
    leaderId: 'npc_trader_leader'
  },
  {
    name: 'Void Explorers',
    type: 'exploration',
    description: 'Exploration society dedicated to discovering new worlds',
    leaderId: 'npc_explorer_leader'
  },
  {
    name: 'Nexus Researchers',
    type: 'research',
    description: 'Scientific organization advancing technology and knowledge',
    leaderId: 'npc_researcher_leader'
  }
];

class GuildSystem {
  async initializeDefaultGuilds() {
    const existingGuilds = await storage.getAllGuilds();

    if (existingGuilds.length === 0) {
      for (const guild of DEFAULT_GUILDS) {
        await storage.createGuild({
          name: guild.name,
          type: guild.type,
          description: guild.description,
          level: 1,
          experience: 0,
          memberCount: 1, // NPC leader
          maxMembers: 100,
          leaderId: guild.leaderId
        });
      }
    }
  }

  async joinGuild(userId: string, guildId: string) {
    const user = await storage.getUser(userId);
    if (!user) {
      throw new Error('User not found');
    }

    if (user.guildId) {
      return {
        success: false,
        error: 'You are already in a guild. Leave your current guild first.'
      };
    }

    const guild = await storage.getGuild(guildId);
    if (!guild) {
      return {
        success: false,
        error: 'Guild not found'
      };
    }

    if (guild.memberCount >= guild.maxMembers) {
      return {
        success: false,
        error: 'Guild is full'
      };
    }

    // Add user to guild
    await storage.updateUser(userId, { guildId });

    // Update guild member count
    await storage.updateGuild(guildId, {
      memberCount: guild.memberCount + 1
    });

    return {
      success: true,
      guild,
      message: `Welcome to ${guild.name}!`
    };
  }

  async leaveGuild(userId: string) {
    const user = await storage.getUser(userId);
    if (!user || !user.guildId) {
      throw new Error('User not in a guild');
    }

    const guild = await storage.getGuild(user.guildId);
    if (guild) {
      await storage.updateGuild(guild.id, {
        memberCount: Math.max(1, guild.memberCount - 1) // Keep at least NPC leader
      });
    }

    await storage.updateUser(userId, { guildId: null });

    return {
      success: true,
      message: `You have left ${guild?.name}`
    };
  }

  async contribute(userId: string, resourceType: string, amount: number) {
    const user = await storage.getUser(userId);
    if (!user || !user.guildId) {
      throw new Error('User not in a guild');
    }

    const guild = await storage.getGuild(user.guildId);
    if (!guild) {
      throw new Error('Guild not found');
    }

    let contributionValue = 0;

    if (resourceType === 'credits') {
      if (user.credits < amount) {
        throw new Error('Insufficient credits');
      }
      await storage.updateUser(userId, {
        credits: user.credits - amount
      });
      contributionValue = amount;
    } else if (resourceType === 'nexium') {
      if (user.nexium < amount) {
        throw new Error('Insufficient nexium');
      }
      await storage.updateUser(userId, {
        nexium: user.nexium - amount
      });
      contributionValue = amount * 10; // Nexium is worth more
    }

    // Add experience to guild
    const expGained = Math.floor(contributionValue / 10);
    const newGuildExp = guild.experience + expGained;
    const newGuildLevel = Math.floor(newGuildExp / 1000) + 1;

    const leveledUp = newGuildLevel > guild.level;

    await storage.updateGuild(guild.id, {
      experience: newGuildExp,
      level: newGuildLevel
    });

    // Award personal experience to contributor
    if (user) {
      const personalExp = Math.floor(expGained * 0.5);
      const currentUserExp = user.experience + personalExp;
      await storage.updateUser(userId, {
        experience: currentUserExp
      });
    }

    return {
      success: true,
      guildExpGained: expGained,
      guildLeveledUp: leveledUp,
      newGuildLevel,
      personalExpGained: Math.floor(expGained * 0.5),
      rewards: leveledUp ? this.calculateGuildLevelRewards(newGuildLevel) : []
    };
  }

  private calculateGuildLevelRewards(level: number) {
    const rewards = [];

    // Every 5 levels, increase max members
    if (level % 5 === 0) {
      rewards.push({
        type: 'member_increase',
        value: 25,
        description: 'Guild member capacity increased by 25'
      });
    }

    // Level-based rewards
    if (level % 10 === 0) {
      rewards.push({
        type: 'credits',
        value: level * 1000,
        description: `${level * 1000} credits for all members`
      });
    }

    return rewards;
  }

  async getGuildRankings() {
    const guilds = await storage.getAllGuilds();
    return guilds
      .sort((a, b) => {
        // Sort by level first, then by experience
        if (b.level !== a.level) {
          return b.level - a.level;
        }
        return b.experience - a.experience;
      })
      .map((guild, index) => ({
        ...guild,
        rank: index + 1
      }));
  }

  async getGuildMembers(guildId: string) {
    return await storage.getGuildMembers(guildId);
  }

  async getGuildVsGuildData() {
    const rankings = await this.getGuildRankings();

    return rankings.map(guild => ({
      id: guild.id,
      name: guild.name,
      type: guild.type,
      level: guild.level,
      memberCount: guild.memberCount,
      rank: guild.rank,
      power: this.calculateGuildPower(guild)
    }));
  }

  private calculateGuildPower(guild: any): number {
    return guild.level * 100 + guild.memberCount * 10 + guild.experience;
  }

  async scheduleGuildWar(guild1Id: string, guild2Id: string) {
    // This would implement guild vs guild warfare
    // For now, return a placeholder
    return {
      scheduled: true,
      battleTime: new Date(Date.now() + 24 * 60 * 60 * 1000), // 24 hours from now
      participants: [guild1Id, guild2Id]
    };
  }
}

export const guildSystem = new GuildSystem();

// Initialize default guilds on startup
guildSystem.initializeDefaultGuilds().catch(console.error);

server/utils/rewardCalculator.ts.content:
interface Reward {
  type: string;
  name: string;
  quantity: number;
  value: number;
}

class RewardCalculator {
  calculateExplorationRewards(explorationType: string, userLevel: number, sensorPower: number): Reward[] {
    const rewards: Reward[] = [];
    const levelMultiplier = 1 + (userLevel - 1) * 0.1;
    const sensorMultiplier = 1 + (sensorPower - 50) * 0.005; // Sensors above 50 provide bonus

    switch (explorationType) {
      case 'exploration':
        rewards.push(...this.generateExplorationRewards(levelMultiplier, sensorMultiplier));
        break;
      case 'hunting':
        rewards.push(...this.generateHuntingRewards(levelMultiplier));
        break;
      case 'artifact_search':
        rewards.push(...this.generateArtifactRewards(levelMultiplier, sensorMultiplier));
        break;
      case 'fishing':
        rewards.push(...this.generateFishingRewards(levelMultiplier));
        break;
      default:
        rewards.push(this.generateBasicReward(levelMultiplier));
    }

    return rewards;
  }

  calculateCombatRewards(enemyDifficulty: number, userLevel: number): Reward[] {
    const rewards: Reward[] = [];
    const difficultyMultiplier = 1 + enemyDifficulty * 0.2;
    const levelMultiplier = 1 + (userLevel - 1) * 0.05;

    // Always give credits
    const creditReward = Math.floor((50 + Math.random() * 100) * difficultyMultiplier * levelMultiplier);
    rewards.push({
      type: 'credits',
      name: 'Combat Pay',
      quantity: 1,
      value: creditReward
    });

    // Chance for materials based on difficulty
    const materialChance = Math.min(0.8, 0.3 + enemyDifficulty * 0.1);
    if (Math.random() < materialChance) {
      rewards.push(...this.generateCombatMaterials(difficultyMultiplier, levelMultiplier));
    }

    // Rare chance for nexium
    if (Math.random() < 0.2) {
      const nexiumAmount = Math.floor((1 + Math.random() * 3) * difficultyMultiplier);
      rewards.push({
        type: 'nexium',
        name: 'Nexium Crystal',
        quantity: nexiumAmount,
        value: nexiumAmount * 100
      });
    }

    return rewards;
  }

  calculateLevelUpRewards(level: number): Reward[] {
    const rewards: Reward[] = [];

    // Credits reward
    const creditAmount = level * 100 + Math.floor(Math.random() * level * 50);
    rewards.push({
      type: 'credits',
      name: 'Level Up Bonus',
      quantity: 1,
      value: creditAmount
    });

    // Nexium every 5 levels
    if (level % 5 === 0) {
      const nexiumAmount = Math.floor(level / 5) + Math.floor(Math.random() * 3);
      rewards.push({
        type: 'nexium',
        name: 'Milestone Nexium',
        quantity: nexiumAmount,
        value: nexiumAmount * 100
      });
    }

    // Special rewards at certain levels
    if (level === 10) {
      rewards.push({
        type: 'component',
        name: 'Advanced Navigation System',
        quantity: 1,
        value: 500
      });
    } else if (level === 20) {
      rewards.push({
        type: 'upgrade',
        name: 'Elite Pilot License',
        quantity: 1,
        value: 1000
      });
    } else if (level === 50) {
      rewards.push({
        type: 'artifact',
        name: 'Commander\'s Insignia',
        quantity: 1,
        value: 5000
      });
    }

    return rewards;
  }

  calculateQuestRewards(questDifficulty: number, questLength: number, userLevel: number): Reward[] {
    const rewards: Reward[] = [];
    const baseMultiplier = questDifficulty * questLength * (1 + userLevel * 0.05);

    // Base credit reward
    const credits = Math.floor((200 + Math.random() * 300) * baseMultiplier);
    rewards.push({
      type: 'credits',
      name: 'Quest Completion',
      quantity: 1,
      value: credits
    });

    // Experience (handled separately in game logic)
    const experience = Math.floor((100 + Math.random() * 200) * baseMultiplier);
    rewards.push({
      type: 'experience',
      name: 'Quest Experience',
      quantity: experience,
      value: 0
    });

    // Chance for special rewards based on difficulty
    if (questDifficulty >= 3) {
      rewards.push(...this.generateSpecialQuestRewards(baseMultiplier));
    }

    return rewards;
  }

  private generateExplorationRewards(levelMultiplier: number, sensorMultiplier: number): Reward[] {
    const rewards: Reward[] = [];
    const totalMultiplier = levelMultiplier * sensorMultiplier;

    // Credits from exploration
    const credits = Math.floor((30 + Math.random() * 70) * totalMultiplier);
    rewards.push({
      type: 'credits',
      name: 'Exploration Data',
      quantity: 1,
      value: credits
    });

    // Common materials
    const materials = ['Iron Ore', 'Silicon', 'Carbon Fiber', 'Aluminum'];
    const materialName = materials[Math.floor(Math.random() * materials.length)];
    const quantity = Math.floor((2 + Math.random() * 4) * levelMultiplier);

    rewards.push({
      type: 'material',
      name: materialName,
      quantity,
      value: quantity * 5
    });

    // Chance for rare discovery
    if (Math.random() < 0.3 * sensorMultiplier) {
      const rareFinds = ['Energy Crystal', 'Rare Metals', 'Quantum Fragment'];
      const rareName = rareFinds[Math.floor(Math.random() * rareFinds.length)];
      rewards.push({
        type: 'artifact',
        name: rareName,
        quantity: 1,
        value: Math.floor(100 * totalMultiplier)
      });
    }

    return rewards;
  }

  private generateHuntingRewards(levelMultiplier: number): Reward[] {
    const rewards: Reward[] = [];

    // Credits from hunting
    const credits = Math.floor((40 + Math.random() * 80) * levelMultiplier);
    rewards.push({
      type: 'credits',
      name: 'Bounty Payment',
      quantity: 1,
      value: credits
    });

    // Hunting materials
    const huntMaterials = ['Organic Compounds', 'Protein Synthesis', 'Biomass', 'Genetic Samples'];
    const materialName = huntMaterials[Math.floor(Math.random() * huntMaterials.length)];
    const quantity = Math.floor((1 + Math.random() * 3) * levelMultiplier);

    rewards.push({
      type: 'material',
      name: materialName,
      quantity,
      value: quantity * 15
    });

    // Rare hunting trophy
    if (Math.random() < 0.2) {
      rewards.push({
        type: 'artifact',
        name: 'Rare Trophy',
        quantity: 1,
        value: Math.floor(200 * levelMultiplier)
      });
    }

    return rewards;
  }

  private generateArtifactRewards(levelMultiplier: number, sensorMultiplier: number): Reward[] {
    const rewards: Reward[] = [];
    const totalMultiplier = levelMultiplier * sensorMultiplier;

    // Higher chance for valuable finds
    if (Math.random() < 0.6) {
      const artifacts = [
        'Ancient Data Core', 'Alien Technology Fragment', 'Quantum Artifact',
        'Temporal Resonator', 'Dimensional Key', 'Psionic Crystal'
      ];

      const artifactName = artifacts[Math.floor(Math.random() * artifacts.length)];
      const value = Math.floor((150 + Math.random() * 350) * totalMultiplier);

      rewards.push({
        type: 'artifact',
        name: artifactName,
        quantity: 1,
        value
      });
    } else {
      // Consolation credits
      const credits = Math.floor((25 + Math.random() * 50) * levelMultiplier);
      rewards.push({
        type: 'credits',
        name: 'Archaeological Survey Fee',
        quantity: 1,
        value: credits
      });
    }

    return rewards;
  }

  private generateFishingRewards(levelMultiplier: number): Reward[] {
    const rewards: Reward[] = [];

    // Fishing always gives something
    const fishTypes = [
      'Space Plankton', 'Quantum Fish', 'Void Eel', 'Stellar Salmon',
      'Nebula Crab', 'Cosmic Shrimp', 'Dark Matter Whale'
    ];

    const fishName = fishTypes[Math.floor(Math.random() * fishTypes.length)];
    const quantity = Math.floor((1 + Math.random() * 2) * levelMultiplier);
    const value = Math.floor((20 + Math.random() * 30) * levelMultiplier);

    rewards.push({
      type: 'material',
      name: fishName,
      quantity,
      value: value * quantity
    });

    // Small credit bonus
    const credits = Math.floor((15 + Math.random() * 25) * levelMultiplier);
    rewards.push({
      type: 'credits',
      name: 'Fishing License Fee',
      quantity: 1,
      value: credits
    });

    return rewards;
  }

  private generateCombatMaterials(difficultyMultiplier: number, levelMultiplier: number): Reward[] {
    const rewards: Reward[] = [];
    const totalMultiplier = difficultyMultiplier * levelMultiplier;

    const combatMaterials = ['Scrap Metal', 'Damaged Electronics', 'Weapon Parts', 'Armor Fragments'];
    const numMaterials = Math.floor(Math.random() * 2) + 1;

    for (let i = 0; i < numMaterials; i++) {
      const materialName = combatMaterials[Math.floor(Math.random() * combatMaterials.length)];
      const quantity = Math.floor((1 + Math.random() * 2) * totalMultiplier);

      rewards.push({
        type: 'material',
        name: materialName,
        quantity,
        value: quantity * 8
      });
    }

    return rewards;
  }

  private generateBasicReward(multiplier: number): Reward {
    return {
      type: 'credits',
      name: 'Basic Reward',
      quantity: 1,
      value: Math.floor((10 + Math.random() * 20) * multiplier)
    };
  }

  private generateSpecialQuestRewards(multiplier: number): Reward[] {
    const rewards: Reward[] = [];

    // Chance for rare components
    if (Math.random() < 0.4) {
      const components = ['Advanced Targeting System', 'Quantum Engine', 'Neural Interface'];
      const componentName = components[Math.floor(Math.random() * components.length)];

      rewards.push({
        type: 'component',
        name: componentName,
        quantity: 1,
        value: Math.floor(300 * multiplier)
      });
    }

    // Chance for nexium
    if (Math.random() < 0.3) {
      const nexiumAmount = Math.floor((2 + Math.random() * 4) * multiplier);
      rewards.push({
        type: 'nexium',
        name: 'Quest Nexium',
        quantity: nexiumAmount,
        value: nexiumAmount * 100
      });
    }

    return rewards;
  }

  calculateMarketPriceModifier(rarity: string, basePrice: number): number {
    const rarityMultipliers = {
      'common': 1.0,
      'uncommon': 1.5,
      'rare': 2.5,
      'epic': 4.0,
      'legendary': 6.5
    };

    const multiplier = rarityMultipliers[rarity as keyof typeof rarityMultipliers] || 1.0;
    const marketFluctuation = 0.8 + Math.random() * 0.4; // ¬±20% market fluctuation

    return Math.floor(basePrice * multiplier * marketFluctuation);
  }

  calculateRepairCosts(shipHealth: number, maxHealth: number, shipTier: number): number {
    const damagePercent = 1 - (shipHealth / maxHealth);
    const baseCost = 50;
    const tierMultiplier = 1 + (shipTier - 1) * 0.5;

    return Math.floor(baseCost * damagePercent * tierMultiplier * maxHealth / 100);
  }

  calculateSalvageValue(itemValue: number, condition: number): number {
    // Condition is 0-1, where 1 is perfect condition
    const salvageRate = 0.2 + condition * 0.3; // 20-50% of original value
    return Math.floor(itemValue * salvageRate);
  }
}

export const rewardCalculator = new RewardCalculator();

