import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Rocket, Home, Map, Users, Store, Menu } from "lucide-react";
import { useState } from "react";
import { Sheet, SheetContent, SheetTrigger } from "@/components/ui/sheet";

export default function Navigation() {
  const [location] = useLocation();
  const [mobileOpen, setMobileOpen] = useState(false);

  const navigation = [
    { name: "Dashboard", href: "/", icon: Home },
    { name: "Fleet", href: "/fleet", icon: Rocket },
    { name: "Explore", href: "/explore", icon: Map },
    { name: "Guild", href: "/guild", icon: Users },
    { name: "Market", href: "/market", icon: Store },
  ];

  const NavLinks = ({ mobile = false, onClose = () => {} }) => (
    <>
      {navigation.map((item) => {
        const Icon = item.icon;
        const isActive = location === item.href;

        return (
          <Link key={item.name} href={item.href}>
            <Button
              variant={isActive ? "default" : "ghost"}
              className={`${mobile ? 'w-full justify-start' : ''} ${
                isActive 
                  ? 'bg-primary text-primary-foreground' 
                  : 'text-foreground hover:text-primary hover:bg-primary/10'
              }`}
              onClick={onClose}
              data-testid={`nav-${item.name.toLowerCase()}`}
            >
              <Icon className="h-4 w-4 mr-2" />
              {item.name}
            </Button>
          </Link>
        );
      })}
    </>
  );

  return (
    <header className="border-b border-border bg-card/50 backdrop-blur-sm sticky top-0 z-50">
      <div className="container mx-auto px-4 py-4">
        <div className="flex items-center justify-between">
          <Link href="/">
            <div className="flex items-center space-x-4 cursor-pointer" data-testid="nav-logo">
              <Rocket className="h-8 w-8 text-primary glow-text" />
              <h1 className="text-2xl font-orbitron font-bold text-primary glow-text">
                STELLAR NEXUS
              </h1>
            </div>
          </Link>

          {/* Desktop Navigation */}
          <nav className="hidden md:flex space-x-6">
            <NavLinks />
          </nav>

          {/* Mobile Navigation */}
          <Sheet open={mobileOpen} onOpenChange={setMobileOpen}>
            <SheetTrigger asChild className="md:hidden">
              <Button variant="ghost" size="icon" data-testid="nav-mobile-menu">
                <Menu className="h-5 w-5" />
                <span className="sr-only">Toggle menu</span>
              </Button>
            </SheetTrigger>
            <SheetContent side="right" className="w-[300px] sm:w-[400px]">
              <div className="flex flex-col space-y-4 mt-8">
                <div className="flex items-center space-x-2 mb-6">
                  <Rocket className="h-6 w-6 text-primary" />
                  <span className="font-orbitron font-bold text-primary">STELLAR NEXUS</span>
                </div>
                <NavLinks mobile={true} onClose={() => setMobileOpen(false)} />
              </div>
            </SheetContent>
          </Sheet>
        </div>
      </div>
    </header>
  );
}

client/src/components/ShipCard.tsx.content:
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Ship } from "@shared/schema";
import { Zap, Package, Shield, Radar, Heart } from "lucide-react";

interface ShipCardProps {
  ship: Ship;
}

export default function ShipCard({ ship }: ShipCardProps) {
  const getShipTypeColor = (type: string) => {
    const colors = {
      scout: 'text-primary',
      fighter: 'text-destructive',
      freighter: 'text-accent',
      explorer: 'text-secondary',
      battlecruiser: 'text-orange-500',
      flagship: 'text-purple-500'
    };
    return colors[type as keyof typeof colors] || 'text-primary';
  };

  const getTierBadgeVariant = (tier: number) => {
    if (tier >= 4) return 'default';
    if (tier >= 3) return 'secondary';
    return 'outline';
  };

  const getTierLabel = (tier: number) => {
    if (tier >= 4) return 'MAX';
    return `T${tier}`;
  };

  return (
    <Card className={`ship-card transition-all duration-300 hover:border-primary/50 ${ship.isActive ? 'border-primary glow-border' : ''}`}>
      <CardContent className="p-4">
        <div className="flex items-center justify-between mb-3">
          <h3 className="font-orbitron font-bold text-lg text-foreground" data-testid={`text-ship-name-${ship.id}`}>
            {ship.variant}
          </h3>
          <div className="flex items-center space-x-2">
            {ship.isActive && (
              <Badge className="bg-green-600 text-white text-xs">ACTIVE</Badge>
            )}
            <Badge variant={getTierBadgeVariant(ship.tier)} data-testid={`badge-ship-tier-${ship.id}`}>
              {getTierLabel(ship.tier)}
            </Badge>
          </div>
        </div>

        <div className={`text-sm text-muted-foreground mb-3 ${getShipTypeColor(ship.type)}`} data-testid={`text-ship-type-${ship.id}`}>
          {ship.type.charAt(0).toUpperCase() + ship.type.slice(1)} - Tier {ship.tier}
        </div>

        <div className="grid grid-cols-2 gap-3 text-xs mb-4">
          <div className="flex items-center space-x-1">
            <Heart className="h-3 w-3 text-red-500" />
            <span className="text-muted-foreground">Health:</span>
            <span className="font-mono text-foreground" data-testid={`text-ship-health-${ship.id}`}>
              {ship.health}/{ship.maxHealth}
            </span>
          </div>
          <div className="flex items-center space-x-1">
            <Zap className="h-3 w-3 text-secondary" />
            <span className="text-muted-foreground">Speed:</span>
            <span className="font-mono text-foreground" data-testid={`text-ship-speed-${ship.id}`}>
              {ship.speed}
            </span>
          </div>
          <div className="flex items-center space-x-1">
            <Package className="h-3 w-3 text-accent" />
            <span className="text-muted-foreground">Cargo:</span>
            <span className="font-mono text-foreground" data-testid={`text-ship-cargo-${ship.id}`}>
              {ship.cargo}
            </span>
          </div>
          <div className="flex items-center space-x-1">
            <Shield className="h-3 w-3 text-destructive" />
            <span className="text-muted-foreground">Weapons:</span>
            <span className="font-mono text-foreground" data-testid={`text-ship-weapons-${ship.id}`}>
              {ship.weapons}
            </span>
          </div>
          <div className="flex items-center space-x-1 col-span-2">
            <Radar className="h-3 w-3 text-primary" />
            <span className="text-muted-foreground">Sensors:</span>
            <span className="font-mono text-foreground" data-testid={`text-ship-sensors-${ship.id}`}>
              {ship.sensors}
            </span>
          </div>
        </div>

        {/* Health bar if ship is damaged */}
        {ship.health < ship.maxHealth && (
          <div className="mb-3">
            <div className="w-full bg-muted rounded-full h-1.5">
              <div 
                className="bg-red-500 h-1.5 rounded-full transition-all" 
                style={{ width: `${(ship.health / ship.maxHealth) * 100}%` }}
              ></div>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/StatBar.tsx.content:
import { cn } from "@/lib/utils";

interface StatBarProps {
  value: number;
  max: number;
  className?: string;
  color?: 'primary' | 'secondary' | 'accent' | 'destructive' | 'success';
}

export default function StatBar({ value, max, className, color = 'primary' }: StatBarProps) {
  const percentage = Math.min(100, Math.max(0, (value / max) * 100));

  const colorClasses = {
    primary: 'stat-bar',
    secondary: 'bg-secondary',
    accent: 'bg-accent',
    destructive: 'bg-destructive',
    success: 'bg-green-500'
  };

  return (
    <div className={cn("w-full bg-muted rounded-full overflow-hidden", className)}>
      <div 
        className={cn(
          "h-full rounded-full transition-all duration-500 ease-in-out",
          colorClasses[color]
        )}
        style={{ width: `${percentage}%` }}
        data-testid="stat-bar-fill"
      />
    </div>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

